import React, { useEffect, useState } from "react";
import "./Employee.css";
import EmployeeData from "../EmployeeData/EmployeeData";
import { useNavigate } from "react-router-dom";

function Employee() {
  const history = useNavigate();
  const [storageEmployee, setStorageEmployee] = useState([]);
  // console.log(JSON.parse(localStorage.getItem("EmployeeData")));
  // let sample = JSON.parse(localStorage.getItem("EmployeeData"));
  // setStorageEmployee(sample);
  useEffect(() => {
    setStorageEmployee(JSON.parse(localStorage.getItem("EmployeeData")));
  }, [storageEmployee]);
  //console.log(storageEmployee, "1");
  const handleDelete = (id) => {
    let index = storageEmployee
      .map(function (e) {
        return e.id;
      })
      .indexOf(id);
    console.log(EmployeeData, "2");
    setStorageEmployee(storageEmployee.splice(index, 1));
    //storageEmployee.splice(index, 1);
    //setStorageEmployee(storageEmployee);
    localStorage.setItem("EmployeeData", JSON.stringify(storageEmployee));
    //EmployeeData.splice(index,1);
    //console.log(id);
    history("/");
  };

  //console.log(JSON.parse(localStorage.getItem("EmployeeData"))[0].fullName);

  return(
    <div>
       {
        storageEmployee.length > 0 ? <>
        (storageEmployee.map((item)) => {

          <div className="employee" key={item.id}>
            <div className="employeeSub">
              <div className="employeeDetails">
                <p className="fullName">{item.fullName}</p>
                <p className="jobTitle">{item.jobTitle}</p>
                <p className="Department">{item.department}</p>
                <p className="gender">{item.gender}</p>
                <p className="age">{item.age}</p>
              </div>
              <button className="edit btnEmployee">Edit</button>
              <button
                className="delete btnEmployee"
                onClick={() => handleDelete(item.id)}
              >
                Delete
              </button>
            </div>
            <hr />
          </div>

      })
        </> : <div>No data has been adde</div>
       }
    </div>
  )
}

export default Employee;
