  const [storageEmployee, setStorageEmployee] = useState([]);
  const [fullName, setFullName] = useState('');
  const [jobTitle, setJobTitle] = useState('');
  const [department, setDepartment] = useState('');
  const [gender, setGender] = useState('');
  const [age, setAge] = useState('');
  const [clicked, setClicked] = useState(true);

  const history = useNavigate();

  const handleEmployeeSubmit = (e)=>{
     e.preventDefault();

  }

  useEffect(() => {
    const sample = JSON.parse(localStorage.getItem("EmployeeData"));
    setStorageEmployee(sample);
  }, []);

  const handleDelete = (id) => {

  };

  //console.log(JSON.parse(localStorage.getItem("EmployeeData"))[0].fullName);

  return (
    <div>
      { 
        storageEmployee.map((item) => {
          return (
            <div className="employee" key={item.id}>
              <div className="employeeSub">
                <div className="employeeDetails">
                  <p className="fullName">{item.fullName}</p>
                  <p className="jobTitle">{item.jobTitle}</p>
                  <p className="Department">{item.department}</p>
                  <p className="gender">{item.gender}</p>
                  <p className="age">{item.age}</p>
                </div>
                <button className="edit btnEmployee">Edit</button>
                <button
                  className="delete btnEmployee"
                  onClick={() => handleDelete(item.id)}
                >
                  Delete
                </button>
              </div>
              <hr />

            </div>
          );

        }
      ) 
      }
      <div className={`form_container ${clicked && "hidden"}`}>
          <form autoComplete="off" className="form-group" onSubmit={handleEmployeeSubmit}>
            <input type="text" className="form-control fullName" required value={fullName} onChange={(e)=> setFullName(e.target.value)}/>
            <input type="text" className="form-control jobTitle" required value={jobTitle} onChange={(e)=> setJobTitle(e.target.value)}/>
            <input type="text" className="form-control Department" required value={department} onChange={(e)=> setDepartment(e.target.value)}/>
            <input type="text" className="form-control gender" required value={gender} onChange={(e)=> setGender(e.target.value)}/>
            <input type="text" className="form-control age" required value={age} onChange={(e)=> setAge(e.target.value)}/>
            <button type="submit" className="btnEmployee submitBtn">Submit</button>
            <hr />
          </form>
        </div>

      <button className="addButton addRowBtn" onClick={() => setClicked(false)}>
        Add
      </button>
    </div>
  );
}

export default Employee;
