import React, { useEffect, useState } from "react";
import "./Employee.css";
import { useNavigate } from "react-router-dom";

const getDataFromLS = () => {
  const data = localStorage.getItem("employeeData");
  if (data) {
    return JSON.parse(data);
  } else {
    return [];
  }
};

function Employee() {
  const [storageEmployee, setStorageEmployee] = useState(getDataFromLS());
  const [fullName, setFullName] = useState("");
  const [jobTitle, setJobTitle] = useState("");
  const [department, setDepartment] = useState("");
  const [gender, setGender] = useState("");
  const [age, setAge] = useState("");
  const [clicked, setClicked] = useState(true);

  const history = useNavigate();

  useEffect(() => {
    localStorage.setItem("employeeData", JSON.stringify(storageEmployee));
  }, [storageEmployee]);

  const handleEmployeeSubmit = (e) => {
    e.preventDefault();
    let employee = {
      fullName,
      jobTitle,
      department,
      gender,
      age,
    };
    setStorageEmployee([...storageEmployee, employee]);
    setFullName("");
    setJobTitle("");
    setDepartment("");
    setGender("");
    setAge("");
    setClicked(true);
  };

  const handleDelete = (e) => {
    cl

  };

  //console.log(JSON.parse(localStorage.getItem("EmployeeData"))[0].fullName);

  return (
    <div>
      {storageEmployee.map((item) => {
        return (
          <div className="employee" key={item.id}>
            <div className="employeeSub">
              <div className="employeeDetails">
                <p className="fullName">{item.fullName}</p>
                <p className="jobTitle">{item.jobTitle}</p>
                <p className="Department">{item.department}</p>
                <p className="gender">{item.gender}</p>
                <p className="age">{item.age}</p>
              </div>
              <button className="edit btnEmployee">Edit</button>
              <button
                className="delete btnEmployee"
                onClick={(e) => handleDelete(e)}
              >
                Delete
              </button>
            </div>
            <hr />
          </div>
        );
      })}
      <div className={`form_container ${clicked && "hidden"}`}>
        <form
          autoComplete="off"
          className="form-group"
          onSubmit={handleEmployeeSubmit}
        >
          <input
            type="text"
            className="form-control fullName"
            required
            value={fullName}
            onChange={(e) => setFullName(e.target.value)}
          />
          <input
            type="text"
            className="form-control jobTitle"
            required
            value={jobTitle}
            onChange={(e) => setJobTitle(e.target.value)}
          />
          <input
            type="text"
            className="form-control Department"
            required
            value={department}
            onChange={(e) => setDepartment(e.target.value)}
          />
          <input
            type="text"
            className="form-control gender"
            required
            value={gender}
            onChange={(e) => setGender(e.target.value)}
          />
          <input
            type="text"
            className="form-control age"
            required
            value={age}
            onChange={(e) => setAge(e.target.value)}
          />
          <button type="submit" className="btnEmployee submitBtn">
            Submit
          </button>
          <hr />
        </form>
      </div>

      <button className="addButton addRowBtn" onClick={() => setClicked(false)}>
        Add
      </button>
    </div>
  );
}

export default Employee;
